import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { useContext } from "react";
import { AppContext } from "../App";

export const Users = () => {
  const [users, setUsers] = useState(useEffect(() => {
    // ابتدا از localStorage کاربران رو می‌گیریم
    const localUsersStr = localStorage.getItem("users");
    console.log(localUsersStr); // برای چک کردن وضعیت داده‌ها

    if (localUsersStr) {
      try {
        let localUsers = JSON.parse(localUsersStr);

        // اگر داده‌ها به درستی آرایه بودند
        if (Array.isArray(localUsers)) {
          // اگر کاربر لاگین شده وجود داشت و هنوز در لیست نیست، اون رو اضافه کن
          if (userDetails && !localUsers.some(u => u.id === userDetails.id)) {
            localUsers.push(userDetails);
          }

          setUsers(localUsers);
          localStorage.setItem("users", JSON.stringify(localUsers));
        } else {
          fetchUsersFromServer();
        }
      } catch (error) {
        console.error("Error parsing localStorage data", error);
        fetchUsersFromServer();
      }
    } else {
      fetchUsersFromServer();
    }
  }, [userDetails]));
  
  const { userDetails } = useContext(AppContext);

  

  const fetchUsersFromServer = () => {
    fetch('https://reqres.in/api/users?page=1') // توجه به پارامتر صفحه
      .then((res) => res.json())
      .then((data) => {
        let serverUsers = data.data || [];
        if (userDetails) {
          serverUsers = [...serverUsers, userDetails];
        }
        setUsers(serverUsers);
        localStorage.setItem("users", JSON.stringify(serverUsers));
      });
  };

  const handleDelete = (user) => {
    const deleteUser = users.filter((u) => u.id !== user.id);
    setUsers(deleteUser);
    localStorage.setItem("users", JSON.stringify(deleteUser));

    const currentUser = JSON.parse(localStorage.getItem("userDetails"));
    if (currentUser && currentUser.id === user.id) {
      localStorage.removeItem("userDetails");
    }
  };

  const handleUpdate = (userId) => {
    const updateUser = users.map((user) => {
      if (user.id === userId.id) {
        return { ...user, email: "newemail@example.com" }; // تغییر ایمیل به دلخواه
      }
      return user;
    });

    setUsers(updateUser);
    localStorage.setItem("users", JSON.stringify(updateUser));
  };

  return (
    <div className="row">
      {users.map((user) => (
        <div className="col-4 p-5 text-center" key={user.id}>
          <img
            src={user.avatar}
            alt=""
            style={{ borderRadius: "50%", width: "50px", height: "50px" }}
          />
          <Link to={`/user/${user.id}`}>
            <h4>
              {user.first_name} {user.last_name}
            </h4>
          </Link>
          <h5>{user.email}</h5>
          <div className="row">
            <div className="col-6 text-center">
              <button className="btn btn-danger btn-sm" onClick={() => handleDelete(user)}>
                DELETE
              </button>
            </div>
            <div className="col-6 text-center">
              <button className="btn btn-warning btn-sm" onClick={() => handleUpdate(user)}>
                UPDATE
              </button>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};
