import { useEffect, useState, useContext } from "react";
import { Link } from "react-router-dom";
import { AppContext } from "../App";

export const Users = () => {
  const [users, setUsers] = useState([]);
  const { userDetails } = useContext(AppContext);

  useEffect(() => {
    const localUsersStr = localStorage.getItem("users");

    if (localUsersStr) {
      try {
        let localUsers = JSON.parse(localUsersStr);

        if (Array.isArray(localUsers)) {
          let updatedUsers = [...localUsers];

          // اگر یوزر جدیدی داشتیم و هنوز اضافه نشده بود
          if (userDetails && !updatedUsers.some(u => u.id === userDetails.id)) {
            updatedUsers.push(userDetails);
          }

          setUsers(updatedUsers);
          localStorage.setItem("users", JSON.stringify(updatedUsers));
        } else {
          fetchUsersFromServer(); // اگر localStorage خراب بود
        }
      } catch (error) {
        console.error("Error parsing localStorage data", error);
        fetchUsersFromServer();
      }
    } else {
      fetchUsersFromServer(); // اگر اصلاً localStorage نداشتیم
    }
  }, [userDetails]);

  const fetchUsersFromServer = () => {
    fetch('https://reqres.in/api/users?page=1')  // ✅ اینجا درستش کردیم
      .then((res) => res.json())
      .then((data) => {
        let serverUsers = data.data || [];

        if (userDetails && !serverUsers.some(u => u.id === userDetails.id)) {
          serverUsers.push(userDetails);
        }

        setUsers(serverUsers);
        localStorage.setItem("users", JSON.stringify(serverUsers));
      })
      .catch((error) => {
        console.error("خطا در دریافت اطلاعات از سرور:", error);
      });
  };

  function handleDelete(user) {
    const updated = users.filter(u => u.id !== user.id);
    setUsers(updated);
    localStorage.setItem("users", JSON.stringify(updated));
  }

  function handleUpdate(user) {
    const updated = users.map(u =>
      u.id === user.id ? { ...u, email: "updated@example.com" } : u
    );
    setUsers(updated);
    localStorage.setItem("users", JSON.stringify(updated));
  }

  return (
    <div className="row">
      {users.map(user => (
        <div className="col-4 p-5 text-center" key={user.id}>
          <img src={user.avatar} alt="" style={{ borderRadius: "50%", width: "50px", height: "50px" }} />
          <Link to={`/user/${user.id}`}>
            <h4>{user.first_name} {user.last_name}</h4>
          </Link>
          <h5>{user.email}</h5>
          <div className="row">
            <div className="col-6 text-center">
              <button className="btn btn-danger btn-sm" onClick={() => handleDelete(user)}>DELETE</button>
            </div>
            <div className="col-6 text-center">
              <button className="btn btn-warning btn-sm" onClick={() => handleUpdate(user)}>UPDATE</button>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};
