import { useEffect, useState, useContext } from "react";
import { Link } from "react-router-dom";
import { AppContext } from "../App";

export const Users = () => {
  const [users, setUsers] = useState([]);
  const { userDetails } = useContext(AppContext);

  useEffect(() => {
    const localUsersStr = localStorage.getItem("users");

    if (localUsersStr) {
      try {
        let localUsers = JSON.parse(localUsersStr);

        // اگر آرایه‌ی ذخیره شده معتبر بود
        if (Array.isArray(localUsers) && localUsers.length > 0) {
          // اگر کاربر لاگین شده جدید باشه، اضافه کن
          if (userDetails && !localUsers.some(u => u.id === userDetails.id)) {
            localUsers.push(userDetails);
            localStorage.setItem("users", JSON.stringify(localUsers));
          }
          setUsers(localUsers);
        } else {
          fetchUsersFromServer();
        }
      } catch (error) {
        console.error("Error parsing localStorage:", error);
        fetchUsersFromServer();
      }
    } else {
      fetchUsersFromServer();
    }
  }, [userDetails]);

  const fetchUsersFromServer = () => {
    fetch('https://reqres.in/api/users?page=1')
      .then(res => res.json())
      .then(data => {
        let fetchedUsers = data.data || [];

        // اضافه کردن کاربر لاگین شده (در صورت وجود)
        if (userDetails && !fetchedUsers.some(u => u.id === userDetails.id)) {
          fetchedUsers.push(userDetails);
        }

        setUsers(fetchedUsers);
        localStorage.setItem("users", JSON.stringify(fetchedUsers));
      })
      .catch(err => console.error("Fetch error:", err));
  };

  const handleDelete = (user) => {
    const filteredUsers = users.filter(u => u.id !== user.id);
    setUsers(filteredUsers);
    localStorage.setItem("users", JSON.stringify(filteredUsers));
  };

  const handleUpdate = (user) => {
    const updatedUsers = users.map(u =>
      u.id === user.id ? { ...u, email: "updated@example.com" } : u
    );
    setUsers(updatedUsers);
    localStorage.setItem("users", JSON.stringify(updatedUsers));
  };

  return (
    <div className="row">
      {users.map((user) => (
        <div className="col-4 p-5 text-center" key={user.id}>
          <img
            src={user.avatar}
            alt=""
            style={{ borderRadius: '50%', width: '50px', height: '50px' }}
          />
          <Link to={`/user/${user.id}`}>
            <h4>{user.first_name} {user.last_name}</h4>
          </Link>
          <h5>{user.email}</h5>
          <div className="row">
            <div className="col-6 text-center">
              <button className="btn btn-danger btn-sm" onClick={() => handleDelete(user)}>DELETE</button>
            </div>
            <div className="col-6 text-center">
              <button className="btn btn-warning btn-sm" onClick={() => handleUpdate(user)}>UPDATE</button>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};
