import { useEffect, useState, useContext } from "react";
import { Link } from "react-router-dom";
import { AppContext } from "../App";

export const Users = () => {
  const [users, setUsers] = useState([]);
  const { userDetails } = useContext(AppContext);

  useEffect(() => {
    const localUsersStr = localStorage.getItem("users");

    try {
      const localUsers = JSON.parse(localUsersStr);

      if (Array.isArray(localUsers) && localUsers.length > 0) {
        let updatedUsers = [...localUsers];

        if (userDetails && !updatedUsers.some(u => u.id === userDetails.id)) {
          updatedUsers.push(userDetails);
          localStorage.setItem("users", JSON.stringify(updatedUsers));
        }

        setUsers(updatedUsers);
      } else {
        fetchUsersFromServer();
      }
    } catch (err) {
      console.error("Error parsing localStorage users:", err);
      fetchUsersFromServer();
    }
  }, [userDetails]);

  const fetchUsersFromServer = () => {

  fetch('https://jsonplaceholder.typicode.com/users')
  .then((res) => {
    if (!res.ok) {
      throw new Error(`HTTP error! status: ${res.status}`);
    }
    return res.json();
  })
  .then((data) => {console.log(data.datg);
  
    const serverUsers = data.data || [];  // فرض کنیم که داده‌ها به صورت آرایه هستند

    setUsers(serverUsers);  // ذخیره در وضعیت
    localStorage.setItem('users', JSON.stringify(serverUsers));  // ذخیره در localStorage
  })
  .catch((error) => {
    console.error('Failed to fetch users:', error);
  });
  }


  const handleDelete = (user) => {
    const filteredUsers = users.filter(u => u.id !== user.id);
    setUsers(filteredUsers);
    localStorage.setItem("users", JSON.stringify(filteredUsers));
    const currentUser = JSON.parse(localStorage.getItem("userDetails"));
    if (currentUser && currentUser.id === user.id) {
      localStorage.removeItem("userDetails");
    }
  };

  const handleUpdate = (user) => {
    const updated = users.map(u =>
      u.id === user.id ? { ...u, email: "ztms@12356hgj.com" } : u
    );
    setUsers(updated);
    localStorage.setItem("users", JSON.stringify(updated));
  };

  return (
    <div className="row">
      {users.filter(user => user != null).map(user => (
        <div className="col-4 p-5 text-center" key={user.id}>
          <img
            src={user.avatar}
            alt=""
            style={{ borderRadius: "50%", width: "50px", height: "50px" }}
          />
          <Link to={`/user/${user.id}`}>
            <h4>{user.first_name} {user.last_name}</h4>
          </Link>
          <h5>{user.email}</h5>
          <div className="row">
            <div className="col-6 text-center">
              <button className="btn btn-danger btn-sm" onClick={() => handleDelete(user)}>DELETE</button>
            </div>
            <div className="col-6 text-center">
              <button className="btn btn-warning btn-sm" onClick={() => handleUpdate(user)}>UPDATE</button>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};
