import { useEffect, useState, useContext } from "react";
import { Link } from "react-router-dom";
import { AppContext } from "../App";

export const Users = () => {
  const [users, setUsers] = useState([]);
  const { userDetails } = useContext(AppContext);

useEffect(() => {
    // چک کردن اینکه آیا داده‌ها در localStorage موجود هستند یا خیر
    const localUsersStr = localStorage.getItem("users");

    if (localUsersStr) {
      try {
        let localUsers = JSON.parse(localUsersStr);

        // اگر داده‌ها به صورت آرایه باشند
        if (Array.isArray(localUsers)) {
          let updatedUser = [...localUsers];
          if (userDetails && !updatedUser.some(u => u.id === userDetails.id)) {
            updatedUser.push(userDetails);
          }

          setUsers(updatedUser);
          localStorage.setItem("users", JSON.stringify(updatedUser));
        } else {
          fetchUsersFromServer(); // اگر داده‌ها به درستی ذخیره نشده بودند، از API بارگیری کنید
        }
      } catch (error) {
        console.error("Error parsing localStorage data", error);
        fetchUsersFromServer(); // در صورت بروز خطا در پارس کردن داده‌ها، از API بارگیری کنید
      }
    } else {
      fetchUsersFromServer(); // اگر داده‌ها در localStorage نبودند، از API بارگیری کنید
    }
  }, [userDetails]); // هر بار که userDetails تغییر کند، دوباره بررسی می‌شود

  const fetchUsersFromServer = () => {
    console.log("Fetching users from API...");

    fetch('https://reqres.in/api/users?page=1') // از API داده‌ها را دریافت می‌کنیم
      .then((res) => res.json())
      .then((data) => {
        let serverUsers = data.data || [];
        
        if (userDetails) {
          serverUsers = [...serverUsers, userDetails]; // در صورت وجود userDetails، آن را اضافه می‌کنیم
        }

        setUsers(serverUsers);
        localStorage.setItem("users", JSON.stringify(serverUsers)); // داده‌ها را در localStorage ذخیره می‌کنیم
      })
      .catch((error) => {
        console.error("Failed to fetch users from API:", error);
      });
  };

  const handleDelete = (user) => {
    const filteredUsers = users.filter(u => u.id !== user.id);
    setUsers(filteredUsers);
    localStorage.setItem("users", JSON.stringify(filteredUsers));
    const currentUser = JSON.parse(localStorage.getItem("userDetails"));
    if (currentUser && currentUser.id === user.id) {
      localStorage.removeItem("userDetails");
    }
  };

  const handleUpdate = (user) => {
    const updated = users.map(u =>
      u.id === user.id ? { ...u, email: "ztms@12356hgj.com" } : u
    );
    setUsers(updated);
    localStorage.setItem("users", JSON.stringify(updated));
  };

  return (
    <div className="row">
      {users.filter(user => user != null).map(user => (
        <div className="col-4 p-5 text-center" key={user.id}>
          <img
            src={user.avatar}
            alt=""
            style={{ borderRadius: "50%", width: "50px", height: "50px" }}
          />
          <Link to={`/user/${user.id}`}>
            <h4>{user.name}</h4>
          </Link>
          <h5>{user.email}</h5>
          <div className="row">
            <div className="col-6 text-center">
              <button className="btn btn-danger btn-sm" onClick={() => handleDelete(user)}>DELETE</button>
            </div>
            <div className="col-6 text-center">
              <button className="btn btn-warning btn-sm" onClick={() => handleUpdate(user)}>UPDATE</button>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};
