import { useForm } from "react-hook-form";
import * as yup from "yup";
import { yupResolver } from "@hookform/resolvers/yup";
import "../component/login.css";
import { useNavigate } from "react-router-dom";
import { AppContext } from "../App";
import { useContext } from "react";

export const Login = () => {
  const { setUsername, setUserDetails } = useContext(AppContext);
  const navigate = useNavigate();

  const schema = yup.object().shape({
    name : yup.string().required("نام الزامی است"),
    email: yup.string().email("ایمیل معتبر نیست").required("ایمیل الزامی است"),
    gender: yup.string().oneOf(["male", "female"], "جنسیت را انتخاب کنید"),
  });

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(schema),
  });

  const onSubmit = (data) => {
    const id = Date.now();
    const avatar = data.gender === "male"
      ? `https://randomuser.me/api/portraits/men/${id % 100}.jpg`
      : `https://randomuser.me/api/portraits/women/${id % 100}.jpg`;

    const newUser = {
      id,
      name: data.name,
      email: data.email,
      gender: data.gender,
      avatar
    };

    setUsername(newUser.name);
    setUserDetails(newUser);
    localStorage.setItem("userDetails", JSON.stringify(newUser));
    navigate("/users");
  };

  return (
    <div className="container mt-5">
      <h2>Login</h2>
      <form onSubmit={handleSubmit(onSubmit)}>
        <div className="mb-3">
          <input
            type="text"
            className="form-control"
            placeholder="Name And Lastname"
            {...register("name")}
          />
          <p className="text-danger">{errors.name?.message}</p>
        </div>
        <div className="mb-3">
          <input
            type="email"
            className="form-control"
            placeholder="Email"
            {...register("email")}
          />
          <p className="text-danger">{errors.email?.message}</p>
        </div>
        <div className="mb-3">
          <select className="form-control" {...register("gender")}>
            <option value="">انتخاب جنسیت</option>
            <option value="male">مرد</option>
            <option value="female">زن</option>
          </select>
          <p className="text-danger">{errors.gender?.message}</p>
        </div>
        <button type="submit" className="btn btn-success w-100">
          ورود
        </button>
      </form>
    </div>
  );
};
