import { useForm } from "react-hook-form";
import * as yup from "yup";
import { yupResolver } from "@hookform/resolvers/yup";
import "../component/login.css";
import { useNavigate } from "react-router-dom";
import { AppContext } from "../App";
import { useContext } from "react";

export const Login = () => {
  const { setUsername, setUserDetails } = useContext(AppContext);
  const navigate = useNavigate();

  const schema = yup.object().shape({
    name : yup.string().required("نام الزامی است"),
    email: yup.string().email("ایمیل معتبر نیست").required("ایمیل الزامی است"),
    gender: yup.string().oneOf(["male", "female"], "جنسیت را انتخاب کنید"),
  });

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(schema),
  });

  const onSubmit = (data) => {
    const id = Date.now();
    const avatar = data.gender === "male"
      ? `https://randomuser.me/api/portraits/men/${id % 100}.jpg`
      : `https://randomuser.me/api/portraits/women/${id % 100}.jpg`;

    const newUser = {
      id,
      name: data.name,
      email: data.email,
      gender: data.gender,
      avatar
    };

    setUsername(newUser.name);
    setUserDetails(newUser);
    localStorage.setItem("userDetails", JSON.stringify(newUser));
    navigate("/users");
  };

  return (
  <div className="container d-flex justify-content-center align-items-center" style={{ minHeight: "100vh" }}>
    <div className="card p-4 shadow" style={{ width: "100%", maxWidth: "400px" }}>
      <h3 className="text-center mb-4">ورود کاربر</h3>
      <form onSubmit={handleSubmit(onSubmit)}>
        <div className="mb-3">
          <label className="form-label">نام و نام خانوادگی</label>
          <input
            type="text"
            className={`form-control ${errors.name ? "is-invalid" : ""}`}
            placeholder="مثلاً علی رضایی"
            {...register("name")}
          />
          <div className="invalid-feedback">{errors.name?.message}</div>
        </div>

        <div className="mb-3">
          <label className="form-label">ایمیل</label>
          <input
            type="email"
            className={`form-control ${errors.email ? "is-invalid" : ""}`}
            placeholder="example@email.com"
            {...register("email")}
          />
          <div className="invalid-feedback">{errors.email?.message}</div>
        </div>

        <div className="mb-4">
          <label className="form-label">جنسیت</label>
          <select className={`form-select ${errors.gender ? "is-invalid" : ""}`} {...register("gender")}>
            <option value="">انتخاب کنید</option>
            <option value="male">مرد</option>
            <option value="female">زن</option>
          </select>
          <div className="invalid-feedback">{errors.gender?.message}</div>
        </div>

        <button type="submit" className="btn btn-primary w-100">ورود</button>
      </form>
    </div>
  </div>
);

};
